import './App.css';
import { io } from 'socket.io-client';
import React, { useState, useEffect } from 'react';
import { LiMensaje, UlMensajes } from './ui-components';

const socket = io('http://localhost:3000');

function App() {
    const [isConnected, setIsConnected] = useState(false);
    const [nuevoMensaje, setNuevoMensaje] = useState('');
    const [mensajes, setMensajes] = useState([]);
    const [file, setFile] = useState(null);

    useEffect(() => {
        socket.on('connect', () => setIsConnected(true));
        socket.on('chat_message', (data) => {
            setMensajes(mensajes => [...mensajes, data]);
        });
        socket.on('file_received', (filename) => {
            // Redirigir al usuario para descargar el archivo
            window.location.href = `files${filename}`;
        });

        return () => {
            socket.off('connect');
            socket.off('chat_message');
        }
    }, []);

    const handleSubmit = (event) => {
        event.preventDefault();
        const newMessage = {
            body: nuevoMensaje,
            from: "Me",
        };
        setMensajes(state => [...state, newMessage]);
        setNuevoMensaje("");
        socket.emit("chat_message", newMessage.body);
    };

    const handleFileUpload = (event) => {
        const fileToUpload = event.target.files[0];
        const reader = new FileReader();
        reader.onload = () => {
            socket.emit("file_upload", reader.result);
        };
        reader.readAsText(fileToUpload);
        setFile(fileToUpload);
    };

    return (
        <div className="App">
            <h2>{isConnected ? 'CONECTADO' : 'NO CONECTADO'}</h2>
            <UlMensajes>
                {mensajes.map((mensaje, i) => (
                    <LiMensaje key={i} isMe={mensaje.from === 'Me'}>{mensaje.from}: {mensaje.body}</LiMensaje>
                ))}
            </UlMensajes>
            <input
                type="text"
                value={nuevoMensaje}
                onChange={e => setNuevoMensaje(e.target.value)}
            />
            <button onClick={handleSubmit}>Enviar</button>

            <input type="file" onChange={handleFileUpload} />
        </div>
    );
}

export default App;
