import React, { useState } from 'react';
import CryptoJS from 'crypto-js';
import './style.css';

export default function Cifrarcomponent() {


  const [text, setText] = useState('');
  const [password, setPassword] = useState('');
  const [encryptedText, setEncryptedText] = useState('');
  const [decryptedText, setDecryptedText] = useState('');

  const cifraDes = () => {
    if (password.length !== 8) {
      alert('Recuerda, la clave debe tener 8 caracteres.');
      return;
    }

    const cipher = CryptoJS.DES.encrypt(text, password).toString();
    setEncryptedText(cipher);
  };

  const descifraDes = () => {
    if (password.length !== 8) {
      alert('Recuerda, la clave debe tener 8 caracteres.');
      return;
    }

    const decipher = CryptoJS.DES.decrypt(encryptedText, password).toString(CryptoJS.enc.Utf8);
    setDecryptedText(decipher);
  };

  const leerDes = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onload = function (fileLoadedEvent) {
      const txtDes = fileLoadedEvent.target.result;
      setText(txtDes);
    };

    reader.readAsText(file, 'UTF-8');
  };
  return (
    <div className="papu">
    <header className="messi">
      <h1>CIFRADO DES</h1>
      <h3>Ayala Gomez NIcolas IÃ±aki</h3>
      <h3>Carlos Emilio Manzano Solis</h3>
    </header>

    <div className="nico">
      <form>
        <p>Selecciona un archivo de texto:</p>
        <input type="file" id="archivodes" onChange={leerDes} className="carlos" />
        <p>Ingresar clave:</p>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Solo 8 caracteres!"
          maxLength="8"
          minLength="8"
          className="pepe"
        />
        <div className="botones">
          <input
            className="form-control"
            type="button"
            id="button"
            value="Cifrar"
            onClick={cifraDes}
            style={{ marginRight: '1.5rem' }}
          />
          <input
            className="form-control"
            type="button"
            id="button"
            value="Descifrar"
            onClick={descifraDes}
            style={{ marginRight: '1.5rem' }}
          />
          <input type="reset" id="button" value="Limpiar" />
          <p id="txtDes">{decryptedText}</p>
        </div>
      </form>
    </div>
  </div>
  )
}
