import React, { useState } from 'react';
import CryptoJS from 'crypto-js';

const cifrados = () => {
  const [text, setText] = useState('');
  const [key, setKey] = useState('');
  const [output, setOutput] = useState('');

  const cifraDes = () => {
    const txt = text;
    console.log("Mensaje a encriptar: " + txt);

    const claveDes = key;
    if (claveDes.length !== 8) {
      alert("Recuerda, solo 8 caracteres.");
      return false;
    }

    const cifra = CryptoJS.DES.encrypt(txt, claveDes).toString();

    descargarArchivo(generarTexto(cifra), "CifradoDES.txt");
  };

  const descifraDes = () => {
    const cifrado = text;
    console.log("Cifrado: " + cifrado);

    const claveDes = key;
    if (claveDes.length !== 8) {
      alert("Aprendé a leer, boludo!!!!");
      return false;
    }

    const desci = CryptoJS.DES.decrypt(cifrado, claveDes);
    const desciText = desci.toString(CryptoJS.enc.Utf8);
    descargarArchivo(generarTexto(desciText), "DescifradoDES.txt");
  };

  const leerDes = (event) => {
    const archivodes = event.target.files[0];

    const readerDes = new FileReader();
    readerDes.onload = function (fileLoadedEvent) {
      const txtDes = fileLoadedEvent.target.result;
      setText(txtDes);
    };

    readerDes.readAsText(archivodes, "UTF-8");
  };

  const descargarArchivo = (contenidoEnBlob, nombreArchivo) => {
    const reader = new FileReader();

    reader.onload = function (event) {
      const save = document.createElement("a");
      save.href = event.target.result;
      save.target = "_blank";

      save.download = nombreArchivo || "CifradoDes.txt";
      const clicEvent = new MouseEvent("click", {
        view: window,
        bubbles: true,
        cancelable: true,
      });

      save.dispatchEvent(clicEvent);

      (window.URL || window.webkitURL).revokeObjectURL(save.href);
    };

    reader.readAsDataURL(contenidoEnBlob);
  };

  const generarTexto = (datos) => {
    const texto = [datos];
    return new Blob(texto, {
      type: "text/plain",
    });
  };

  return (
    <p>cositas</p>
  )
};

export default cifrados;
