import React, { useState } from 'react';
import CryptoJS from 'crypto-js';

const cifrados = () => {
  const [text, setText] = useState('');
  const [key, setKey] = useState('');
  const [output, setOutput] = useState('');

  const cifraDes = () => {
    const txt = text;
    console.log("Mensaje a encriptar: " + txt);

    const claveDes = key;
    if (claveDes.length !== 8) {
      alert("Recuerda, solo 8 caracteres.");
      return false;
    }

    const cifra = CryptoJS.DES.encrypt(txt, claveDes).toString();

    descargarArchivo(generarTexto(cifra), "CifradoDES.txt");
  };

  const descifraDes = () => {
    const cifrado = text;
    console.log("Cifrado: " + cifrado);

    const claveDes = key;
    if (claveDes.length !== 8) {
      alert("Aprendé a leer, boludo!!!!");
      return false;
    }

    const desci = CryptoJS.DES.decrypt(cifrado, claveDes);
    const desciText = desci.toString(CryptoJS.enc.Utf8);
    descargarArchivo(generarTexto(desciText), "DescifradoDES.txt");
  };

  const leerDes = (event) => {
    const archivodes = event.target.files[0];

    const readerDes = new FileReader();
    readerDes.onload = function (fileLoadedEvent) {
      const txtDes = fileLoadedEvent.target.result;
      setText(txtDes);
    };

    readerDes.readAsText(archivodes, "UTF-8");
  };

  const descargarArchivo = (contenidoEnBlob, nombreArchivo) => {
    const reader = new FileReader();

    reader.onload = function (event) {
      const save = document.createElement("a");
      save.href = event.target.result;
      save.target = "_blank";

      save.download = nombreArchivo || "CifradoDes.txt";
      const clicEvent = new MouseEvent("click", {
        view: window,
        bubbles: true,
        cancelable: true,
      });

      save.dispatchEvent(clicEvent);

      (window.URL || window.webkitURL).revokeObjectURL(save.href);
    };

    reader.readAsDataURL(contenidoEnBlob);
  };

  const generarTexto = (datos) => {
    const texto = [datos];
    return new Blob(texto, {
      type: "text/plain",
    });
  };

  return (
    <div className="papu">
      <header>
        <div className="messi">
          <h1>CIFRADO DES</h1>
          <h3>Ayala Gomez NIcolas Iñaki</h3>
          <h3>Carlos Emilio Manzano Solis</h3>
        </div>
      </header>

      <div className="nico">
        <form>
          <p>Selecciona un archivo de texto:</p>
          <input
            type="file"
            id="archivodes"
            onChange={leerDes}
            className="carlos"
          />
          <p>Ingresar clave:</p>
          <input
            type="password"
            id="clavedes"
            placeholder="Solo 8 caracteres!"
            maxLength="8"
            minLength="8"
            className="pepe"
            value={key}
            onChange={(e) => setKey(e.target.value)}
          />
          <div className="botones">
            <input
              className="form-control"
              type="button"
              value="Cifrar"
              onClick={cifraDes}
              style={{ marginRight: '1.5rem' }}
            />
            <input
              className="form-control"
              type="button"
              value="Descifrar"
              onClick={descifraDes}
              style={{ marginRight: '1.5rem' }}
            />
            <input type="reset" value="Limpiar" />
            <p id="txtDes">{output}</p>
          </div>
        </form>
      </div>
    </div>
  );
};

export default cifrados;
