import React, { useState, useEffect } from 'react';
import CryptoJS from 'crypto-js';
import '../Style/style.css';

export default function Cifrar() {
  const [file, setFile] = useState(null);
  const [password, setPassword] = useState('');
  const [encryptedText, setEncryptedText] = useState('');
  const [decryptedText, setDecryptedText] = useState('');
  const [socket, setSocket] = useState(null);

  useEffect(() => {
    // Configurar la conexión WebSocket al montar el componente
    const newSocket = new WebSocket('ws://localhost:3000');
    setSocket(newSocket);

    // Cleanup al desmontar el componente
    return () => {
      newSocket.close();
    };
  }, []);

  useEffect(() => {
    if (!socket) return;

    // Función que maneja el evento de mensaje del servidor WebSocket
    const handleWebSocketMessage = (event) => {
      const data = JSON.parse(event.data);
      if (data.action === 'claveLength') {
        if (data.length > 8) {
          alert('La clave debe tener 8 caracteres o menos');
        }
      }
    };

    // Configurar escuchador de mensajes WebSocket
    socket.addEventListener('message', handleWebSocketMessage);

    // Cleanup al desmontar el componente
    return () => {
      socket.removeEventListener('message', handleWebSocketMessage);
    };
  }, [socket]);

  const handleChange = (event) => {
    const claveInput = event.target.value;
    setPassword(claveInput);

    // Enviar la longitud de la clave al servidor WebSocket
    if (socket) {
      socket.send(JSON.stringify({ action: 'claveLength', length: claveInput.length }));
    }
  };

  const handleFileChange = (event) => {
    const selectedFile = event.target.files[0];
    setFile(selectedFile);

    if (selectedFile) {
      const reader = new FileReader();
      reader.onload = function (fileLoadedEvent) {
        const txt = fileLoadedEvent.target.result;
        setDecryptedText(txt);
      };
      reader.readAsText(selectedFile, 'UTF-8');
    }
  };

  const cifrar = () => {
    if (password.length !== 8) {
      alert('Recuerda, la clave debe tener 8 caracteres.');
      return;
    }

    if (file) {
      const reader = new FileReader();
      reader.onload = function (fileLoadedEvent) {
        const txt = fileLoadedEvent.target.result;
        const cipher = CryptoJS.DES.encrypt(txt, password).toString();
        setEncryptedText(cipher);
      };
      reader.readAsText(file, 'UTF-8');
    }
  };

  const descifrar = () => {
    if (password.length !== 8) {
      alert('Recuerda, la clave debe tener 8 caracteres.');
      return;
    }

    if (encryptedText) {
      const decipher = CryptoJS.DES.decrypt(encryptedText, password).toString(CryptoJS.enc.Utf8);
      setDecryptedText(decipher);
    }
  };

  return (
    <div className="papu">
      <header className="messi">
        <h1>CIFRADO DES</h1>
        <h3>Ayala Gomez NIcolas Iñaki</h3>
        <h3>Carlos Emilio Manzano Solis</h3>
      </header>

      <div className="nico">
        <form>
          <p>Selecciona un archivo de texto:</p>
          <input type="file" id="archivodes" onChange={handleFileChange} className="carlos" />
          <p>Ingresar clave:</p>
          <input
            type="password"
            value={password}
            onChange={handleChange}
            placeholder="Solo 8 caracteres!"
            maxLength="8"
            minLength="8"
            className="pepe"
          />
          <div className="botones">
            <input
              className="form-control"
              type="button"
              id="button"
              value="Cifrar"
              onClick={cifrar}
              style={{ marginRight: '1.5rem' }}
            />
            <input
              className="form-control"
              type="button"
              id="button"
              value="Descifrar"
              onClick={descifrar}
              style={{ marginRight: '1.5rem' }}
            />
            <input type="reset" id="button" value="Limpiar" />
            <p id="txtDes">{decryptedText}</p>
          </div>
        </form>
      </div>
    </div>
  );
}
